    series: [{
        symbolRepeat: ... // 对 data 中所有数据项生效。
        data: [23, 56]
    }]
    或者
    series: [{
        data: [{
            value: 23
            symbolRepeat: ... // 只对此数据项生效
        }, {
            value: 56
            symbolRepeat: ... // 只对此数据项生效
        }]
    }]




