    renderItem: function (params, api) {
        return {
            type: 'xxx',
            extra: {
                mmm: ...,
                nnn: ...,
                ppp: ...,
                qqq: ...,
            },
            // `extra` 下所有属性开启过渡动画。
            transition: 'extra',
        };
    }

