                ppp: ...,
                qqq: ...,
                // 这两个属性会开启过渡动画。
                transition: ['mmm', 'ppp']
            }
        };
    }


我们这样可以指定 shape 下所有属性开启过渡动画： 

    renderItem: function (params, api) {
        return {
            type: 'xxx',
            shape: {
                mmm: ...,
                nnn: ...,
                ppp: ...,
                qqq: ...,
            },
            // `shape` 下所有属性开启过渡动画。
            transition: 'shape',
        };
    }

