        ].join(&#39;\n&#39;),

        rich: {
            a: {
                color: &#39;red&#39;,
                lineHeight: 10
            },
            b: {
                backgroundColor: {
                    image: &#39;xxx/xxx.jpg&#39;
                },
                height: 40
            },
            x: {
                fontSize: 18,
                fontFamily: &#39;Microsoft YaHei&#39;,
                borderColor: &#39;#449933&#39;,
                borderRadius: 4
            },
            ...
        }
    }


